def scale_shift_line(line, x_shift, y_shift, x_amp, y_amp):
    """ Shift and amplify an entire line
    """
    xy_line = []
    for i in range(0, len(line), 2):
         xx = line[i] * x_amp + x_shift
         yy = line[i+1] * y_amp + y_shift
         xy_line.append(xx)
         xy_line.append(yy)
    return  xy_line

